#!/usr/bin/env node

/**
 * Cline-powered API Route Generator
 * Automatically generates Next.js API routes with TypeScript
 */

const fs = require('fs');
const path = require('path');

function generateAPIRoute(routeName) {
  return `import { NextRequest, NextResponse } from 'next/server';

/**
 * ${routeName} API Route
 * Auto-generated by Cline CLI automation
 */

export async function GET(request: NextRequest) {
  try {
    const data = {
      message: '${routeName} endpoint',
      timestamp: new Date().toISOString(),
      status: 'success'
    };

    return NextResponse.json(data, { status: 200 });
  } catch (error) {
    console.error('${routeName} GET error:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    
    const response = {
      message: '${routeName} POST received',
      data: body,
      timestamp: new Date().toISOString(),
    };

    return NextResponse.json(response, { status: 201 });
  } catch (error) {
    console.error('${routeName} POST error:', error);
    return NextResponse.json(
      { error: 'Invalid request' },
      { status: 400 }
    );
  }
}
`;
}

const routeName = process.argv[2];

if (!routeName) {
  console.error('‚ùå Usage: node api-generator.js RouteName');
  process.exit(1);
}

const apiDir = path.join(__dirname, '..', 'app', 'api', routeName.toLowerCase());
fs.mkdirSync(apiDir, { recursive: true });

const routeFile = path.join(apiDir, 'route.ts');

if (fs.existsSync(routeFile)) {
  console.error(`‚ùå API route ${routeName} already exists`);
  process.exit(1);
}

fs.writeFileSync(routeFile, generateAPIRoute(routeName));

console.log(`‚úÖ Generated: app/api/${routeName.toLowerCase()}/route.ts`);
console.log(`\nüéâ API endpoint ready!`);
console.log(`üìç Access at: http://localhost:3000/api/${routeName.toLowerCase()}`);
